import { MdRefresh, MdWork, MdSettings, MdClose } from 'react-icons/md';
import { AiTwotoneRest } from 'react-icons/ai';
import { useState, useEffect } from 'react';
import './App.css';

const ShowTime = ( {time} ) => {
  const minute = Math.floor(time / 60);
  const second = time % 60;

  return (
    <span className="timer">
      {minute < 10 ? '0'+minute : minute} : {second < 10 ? '0'+second : second}
    </span>
  )
}

const modes = {
  work: 0,
  rest: 1,
  setting: 2
}

export default function App() {
  // 3000s = 50mins for work, 600s = 10min for break
  const [ workTime, setWorkTime ] = useState(3000);
  const [ restTime, setRestTime ] = useState(600);
  const [ timer, setTimer ] = useState(workTime);
  const [ mode, setMode ] = useState(modes.work);
  const [ isBreak, setIsBreak ] = useState(false);

  useEffect(() => {
    const interval = setInterval(() => {
      setTimer(t => t-1);
    }, 1000);

    return () => clearInterval(interval);
  }, []);

  return (
    <>
      <div className="main workMain">
        <div className="btnContainer">
          <MdRefresh 
            className="btn" 
            onClick={()=>{
              mode == modes.work ? setTimer(workTime) : setTimer(restTime)
            }}
          />
          <MdSettings 
            className="btn"/>
        </div>
        <ShowTime time={timer} />
        {isBreak ? <MdWork className="btn"/> : <AiTwotoneRest className="btn"/>}
      </div>
      <div className="main settingMain">
        <div className="btnContainer">
          <MdRefresh
            className="btn stBtn" 
            onClick={()=>{
            }}
          />
        </div>
        <div className="textContainer">
          <span>work</span>
          <input 
            type="text" 
            placeholder={workTime}
            className="inText"   
            style={null}
          />
        </div>
        <div className="textContainer">
          <span>rest</span>
          <input type="text" value={restTime}/>
        </div>
        <MdClose className="btn stBtn"/>
      </div>
    </>
  );
}
