import { MdCached, MdFreeBreakfast, MdWork, MdSettings  } from 'react-icons/md';
import { AiTwotoneRest } from 'react-icons/ai';
import { useState, useEffect } from 'react';
import './App.css';

const ShowTime = ( {time} ) => {
  const minute = Math.floor(time / 60);
  const second = time % 60;

  return (
    <span className="timer">
      {minute < 10 ? '0'+minute : minute} : {second < 10 ? '0'+second : second}
    </span>
  )
}

export default function App() {
  // 3000 seconds = 50mins
  const [ timer, setTimer ] = useState(3000);
  const [ isBreak, setIsBreak ] = useState(false);

  useEffect(() => {
    const interval = setInterval(() => {
      setTimer(timer => timer-1);
    }, 1000);

    return () => clearInterval(interval);
  }, []);

  return (
    <>
      <div className="main">
        <span className="btn" id="resetBtn">reset</span>
        <ShowTime time={timer} />
        <span className="btn" id="breakBtn">{isBreak? 'work' : 'break'}</span>
      </div>
    </>
  );
}
