import { MdCached, MdWork, MdSettings  } from 'react-icons/md';
import { AiTwotoneRest } from 'react-icons/ai';
import { useState, useEffect } from 'react';
import './App.css';

const ShowTime = ( {time} ) => {
  const minute = Math.floor(time / 60);
  const second = time % 60;

  return (
    <span className="timer">
      {minute < 10 ? '0'+minute : minute} : {second < 10 ? '0'+second : second}
    </span>
  )
}

export default function App() {
  // 3000s = 50mins for work, 600s = 10min for break
  const [ workTime, setWorkTime ] = useState(3000);
  const [ breakTime, setBreakTime ] = useState(600);
  const [ workTimer, setWorkTimer ] = useState(workTime);
  const [ breakTimer, setBreakTimer ] = useState(breakTime);
  const [ isBreak, setIsBreak ] = useState(false);

  useEffect(() => {
    const interval = setInterval(() => {
      setTimer(timer => timer-1);
    }, 1000);

    return () => clearInterval(interval);
  }, []);

  return (
    <>
      <div className="main">
        <div className="btnContainer">
          <MdCached className="btn" onClick={()=>setTimer(3000)}/>
          <MdSettings className="btn"/>
        </div>
        <ShowTime time={timer} />
        {isBreak ? <MdWork className="btn"/> : <AiTwotoneRest className="btn"/>}
      </div>
    </>
  );
}
